// JVM Heap Memory Areas
// 1. Young Generation (Eden, S0, S1)
// 2. Old Generation (Tenured)
// 3. Metaspace (Java 8+) / PermGen (Java 7-)

// Memory allocation example
public class MemoryExample {
    private static final int MEGABYTE = 1024 * 1024;

    public static void main(String[] args) {
        // Large object allocation in Eden space
        byte[] largeArray = new byte[10 * MEGABYTE];

        // Monitor memory usage
        Runtime runtime = Runtime.getRuntime();
        long maxMemory = runtime.maxMemory();
        long totalMemory = runtime.totalMemory();
        long freeMemory = runtime.freeMemory();

        System.out.println("Max Memory: " + maxMemory / MEGABYTE + " MB");
        System.out.println("Total Memory: " + totalMemory / MEGABYTE + " MB");
        System.out.println("Free Memory: " + freeMemory / MEGABYTE + " MB");
    }
}

// Garbage Collection Types
// 1. Serial GC: Single-threaded, good for small applications
// 2. Parallel GC: Multi-threaded, default for server applications
// 3. G1 GC: Low-latency, good for large heap sizes
// 4. ZGC/Shenandoah: Ultra-low latency

// JVM Flags for GC tuning
// -XX:+UseG1GC                    // Enable G1 GC
// -XX:MaxGCPauseMillis=200        // Target pause time
// -XX:G1HeapRegionSize=16m        // Region size
// -XX:+PrintGCDetails             // Print GC details

public class GCExample {
    private List<String> data = new ArrayList<>();

    public void createMemoryPressure() {
        // Create objects that will be garbage collected
        for (int i = 0; i < 1000000; i++) {
            data.add("String " + i);

            // Force GC every 100k iterations
            if (i % 100000 == 0) {
                System.gc(); // Hint to JVM
            }
        }
    }

    // Memory leak example - objects not eligible for GC
    private static List<Object> memoryLeak = new ArrayList<>();

    public void createMemoryLeak() {
        // Objects added to static list never get GC'd
        memoryLeak.add(new Object());
    }
}

// Common Memory Leak Patterns

// 1. Static Collections
public class StaticCollectionLeak {
    private static List<Object> staticList = new ArrayList<>();

    public void addToStaticList(Object obj) {
        staticList.add(obj); // Never removed!
    }
}

// 2. Unclosed Resources
public class ResourceLeak {
    public void readFile() {
        FileInputStream fis = null;
        try {
            fis = new FileInputStream("file.txt");
            // Process file
        } catch (IOException e) {
            e.printStackTrace();
        }
        // Missing finally block to close fis!
    }

    // Better approach with try-with-resources
    public void readFileCorrectly() {
        try (FileInputStream fis = new FileInputStream("file.txt")) {
            // Process file
        } catch (IOException e) {
            e.printStackTrace();
        }
        // Automatically closed
    }
}

// 3. Event Listeners
public class ListenerLeak {
    private List<EventListener> listeners = new ArrayList<>();

    public void addListener(EventListener listener) {
        listeners.add(listener);
    }

    // Missing removeListener method!
    public void removeListener(EventListener listener) {
        listeners.remove(listener);
    }
}

// Memory profiling with WeakReference
public class MemoryProfiling {
    public static void main(String[] args) {
        Object obj = new Object();
        WeakReference<Object> weakRef = new WeakReference<>(obj);

        // Check if object is still in memory
        if (weakRef.get() != null) {
            System.out.println("Object still in memory");
        }

        obj = null; // Remove strong reference
        System.gc(); // Force GC

        if (weakRef.get() == null) {
            System.out.println("Object has been garbage collected");
        }
    }
}

import java.util.concurrent.atomic.*;

// Lock-free counter using AtomicInteger
public class LockFreeCounter {
    private final AtomicInteger count = new AtomicInteger(0);

    public int increment() {
        return count.incrementAndGet();
    }

    public int decrement() {
        return count.decrementAndGet();
    }

    public int get() {
        return count.get();
    }

    // Compare and swap example
    public boolean compareAndSet(int expected, int newValue) {
        return count.compareAndSet(expected, newValue);
    }
}

// Lock-free stack implementation
public class LockFreeStack<T> {
    private final AtomicReference<Node<T>> head = new AtomicReference<>();

    private static class Node<T> {
        final T data;
        final AtomicReference<Node<T>> next;

        Node(T data) {
            this.data = data;
            this.next = new AtomicReference<>();
        }
    }

    public void push(T item) {
        Node<T> newNode = new Node<>(item);
        Node<T> currentHead;

        do {
            currentHead = head.get();
            newNode.next.set(currentHead);
        } while (!head.compareAndSet(currentHead, newNode));
    }

    public T pop() {
        Node<T> currentHead;
        Node<T> newHead;

        do {
            currentHead = head.get();
            if (currentHead == null) {
                return null;
            }
            newHead = currentHead.next.get();
        } while (!head.compareAndSet(currentHead, newHead));

        return currentHead.data;
    }
}

import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicInteger;

// Custom ThreadFactory
public class CustomThreadFactory implements ThreadFactory {
    private final AtomicInteger threadNumber = new AtomicInteger(1);
    private final String namePrefix;

    public CustomThreadFactory(String namePrefix) {
        this.namePrefix = namePrefix;
    }

    @Override
    public Thread newThread(Runnable r) {
        Thread t = new Thread(r, namePrefix + threadNumber.getAndIncrement());
        t.setDaemon(false);
        t.setPriority(Thread.NORM_PRIORITY);
        return t;
    }
}

// Custom RejectedExecutionHandler
public class CustomRejectedExecutionHandler implements RejectedExecutionHandler {
    @Override
    public void rejectedExecution(Runnable r, ThreadPoolExecutor executor) {
        if (!executor.isShutdown()) {
            System.out.println("Task rejected: " + r.toString());
            // Could implement retry logic, logging, or fallback mechanism
        }
    }
}

// Advanced Thread Pool Configuration
public class AdvancedThreadPool {

    public static void main(String[] args) {
        // Custom thread pool with monitoring
        ThreadPoolExecutor executor = new ThreadPoolExecutor(
            2,                              // corePoolSize
            10,                             // maximumPoolSize
            60L,                            // keepAliveTime
            TimeUnit.SECONDS,               // timeUnit
            new LinkedBlockingQueue<>(100), // workQueue
            new CustomThreadFactory("CustomPool-"), // threadFactory
            new CustomRejectedExecutionHandler()  // rejectedExecutionHandler
        );

        // Add shutdown hook
        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            executor.shutdown();
            try {
                if (!executor.awaitTermination(60, TimeUnit.SECONDS)) {
                    executor.shutdownNow();
                }
            } catch (InterruptedException e) {
                executor.shutdownNow();
            }
        }));

        // Submit tasks
        for (int i = 0; i < 20; i++) {
            final int taskId = i;
            executor.submit(() -> {
                System.out.println("Task " + taskId + " executed by " + Thread.currentThread().getName());
                try {
                    Thread.sleep(1000);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            });
        }

        // Monitor thread pool
        ScheduledExecutorService monitor = Executors.newScheduledThreadPool(1);
        monitor.scheduleAtFixedRate(() -> {
            System.out.println("Active threads: " + executor.getActiveCount());
            System.out.println("Completed tasks: " + executor.getCompletedTaskCount());
            System.out.println("Queue size: " + executor.getQueue().size());
        }, 0, 2, TimeUnit.SECONDS);
    }
}

import java.util.concurrent.*;
import java.util.concurrent.atomic.*;
import java.util.*;

// ConcurrentHashMap advanced usage
public class ConcurrentCollectionsExample {

    public static void main(String[] args) {
        // ConcurrentHashMap with custom operations
        ConcurrentHashMap<String, AtomicInteger> map = new ConcurrentHashMap<>();

        // Thread-safe increment operation
        map.computeIfAbsent("counter", k -> new AtomicInteger(0)).incrementAndGet();

        // Atomic operations
        map.merge("counter", new AtomicInteger(1), (oldVal, newVal) -> {
            oldVal.addAndGet(newVal.get());
            return oldVal;
        });

        // ConcurrentLinkedQueue for high-throughput scenarios
        ConcurrentLinkedQueue<String> queue = new ConcurrentLinkedQueue<>();

        // Producer
        ExecutorService producer = Executors.newFixedThreadPool(2);
        for (int i = 0; i < 10; i++) {
            final int item = i;
            producer.submit(() -> {
                queue.offer("Item-" + item);
                System.out.println("Produced: Item-" + item);
            });
        }

        // Consumer
        ExecutorService consumer = Executors.newFixedThreadPool(2);
        for (int i = 0; i < 10; i++) {
            consumer.submit(() -> {
                String item = queue.poll();
                if (item != null) {
                    System.out.println("Consumed: " + item);
                }
            });
        }

        // CopyOnWriteArrayList for read-heavy scenarios
        CopyOnWriteArrayList<String> cowList = new CopyOnWriteArrayList<>();
        cowList.addAll(Arrays.asList("A", "B", "C"));

        // Concurrent readers
        ExecutorService readers = Executors.newFixedThreadPool(5);
        for (int i = 0; i < 5; i++) {
            readers.submit(() -> {
                for (String item : cowList) {
                    System.out.println("Read: " + item);
                }
            });
        }

        // Writer (creates new copy)
        cowList.add("D");

        // BlockingQueue for producer-consumer pattern
        BlockingQueue<Integer> blockingQueue = new ArrayBlockingQueue<>(10);

        // Producer with blocking put
        new Thread(() -> {
            try {
                for (int i = 0; i < 20; i++) {
                    blockingQueue.put(i);
                    System.out.println("Put: " + i);
                }
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }).start();

        // Consumer with blocking take
        new Thread(() -> {
            try {
                while (true) {
                    Integer item = blockingQueue.take();
                    System.out.println("Took: " + item);
                    Thread.sleep(100);
                }
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }).start();
    }
}

// 1. Eager Initialization (Thread-safe but not lazy)
public class EagerSingleton {
    private static final EagerSingleton INSTANCE = new EagerSingleton();

    private EagerSingleton() {}

    public static EagerSingleton getInstance() {
        return INSTANCE;
    }
}

// 2. Lazy Initialization (Not thread-safe)
public class LazySingleton {
    private static LazySingleton instance;

    private LazySingleton() {}

    public static LazySingleton getInstance() {
        if (instance == null) {
            instance = new LazySingleton(); // Race condition!
        }
        return instance;
    }
}

// 3. Thread-safe Lazy Initialization with synchronized
public class SynchronizedSingleton {
    private static SynchronizedSingleton instance;

    private SynchronizedSingleton() {}

    public static synchronized SynchronizedSingleton getInstance() {
        if (instance == null) {
            instance = new SynchronizedSingleton();
        }
        return instance;
    }
}

// 4. Double-checked Locking (Volatile required)
public class DoubleCheckedSingleton {
    private static volatile DoubleCheckedSingleton instance;

    private DoubleCheckedSingleton() {}

    public static DoubleCheckedSingleton getInstance() {
        if (instance == null) {
            synchronized (DoubleCheckedSingleton.class) {
                if (instance == null) {
                    instance = new DoubleCheckedSingleton();
                }
            }
        }
        return instance;
    }
}

// 5. Bill Pugh Solution (Inner static class)
public class BillPughSingleton {
    private BillPughSingleton() {}

    private static class SingletonHelper {
        private static final BillPughSingleton INSTANCE = new BillPughSingleton();
    }

    public static BillPughSingleton getInstance() {
        return SingletonHelper.INSTANCE;
    }
}

