import java.util.concurrent.*;
import java.util.concurrent.atomic.*;

// Generic Observer interface
public interface Observer<T> {
    void update(T data);
}

// Generic Subject interface
public interface Subject<T> {
    void addObserver(Observer<T> observer);
    void removeObserver(Observer<T> observer);
    void notifyObservers(T data);
}

// Thread-safe Observable implementation
public class ThreadSafeObservable<T> implements Subject<T> {
    private final CopyOnWriteArrayList<Observer<T>> observers = new CopyOnWriteArrayList<>();
    private final AtomicBoolean changed = new AtomicBoolean(false);

    @Override
    public void addObserver(Observer<T> observer) {
        if (observer != null && !observers.contains(observer)) {
            observers.add(observer);
        }
    }

    @Override
    public void removeObserver(Observer<T> observer) {
        observers.remove(observer);
    }

    @Override
    public void notifyObservers(T data) {
        if (changed.get()) {
            for (Observer<T> observer : observers) {
                observer.update(data);
            }
            changed.set(false);
        }
    }

    public void setChanged() {
        changed.set(true);
    }

    public void clearChanged() {
        changed.set(false);
    }
}

// Concrete implementation
public class NewsAgency extends ThreadSafeObservable<String> {
    private String news;

    public void setNews(String news) {
        this.news = news;
        setChanged();
        notifyObservers(news);
    }

    public String getNews() {
        return news;
    }
}

// Concrete observers
public class NewsChannel implements Observer<String> {
    private final String name;

    public NewsChannel(String name) {
        this.name = name;
    }

    @Override
    public void update(String news) {
        System.out.println(name + " received news: " + news);
    }
}

// Usage example
