// JIT Optimization Examples

// 1. Method Inlining
public class InliningExample {
    public int calculate(int a, int b) {
        return add(a, b) * multiply(a, b);
    }

    private int add(int a, int b) {
        return a + b; // Will be inlined by JIT
    }

    private int multiply(int a, int b) {
        return a * b; // Will be inlined by JIT
    }
}

// 2. Loop Unrolling
public class LoopUnrolling {
    public int sumArray(int[] array) {
        int sum = 0;
        // JIT will unroll this loop for better performance
        for (int i = 0; i < array.length; i++) {
            sum += array[i];
        }
        return sum;
    }

    // Manual loop unrolling for critical sections
    public int sumArrayUnrolled(int[] array) {
        int sum = 0;
        int i = 0;

        // Unroll by 4
        for (; i < array.length - 3; i += 4) {
            sum += array[i] + array[i + 1] + array[i + 2] + array[i + 3];
        }

        // Handle remaining elements
        for (; i < array.length; i++) {
            sum += array[i];
        }

        return sum;
    }
}

// 3. Dead Code Elimination
public class DeadCodeElimination {
    private static final boolean DEBUG = false;

    public void processData(String data) {
        if (DEBUG) {
            // This code will be eliminated by JIT when DEBUG is false
            System.out.println("Debug: Processing " + data);
            logToFile(data);
        }

        // Actual processing
        performActualWork(data);
    }

    private void logToFile(String data) {
        // Debug logging code
    }

    private void performActualWork(String data) {
        // Real work
    }
}

// 4. Branch Prediction Optimization
public class BranchPrediction {
    // Good: Predictable branch pattern
    public int processSortedArray(int[] sortedArray, int target) {
        int count = 0;
        for (int value : sortedArray) {
            if (value < target) { // Predictable: mostly true
                count++;
            } else {
                break; // Predictable: false when we hit target or higher
            }
        }
        return count;
    }

    // Bad: Unpredictable branch pattern
    public int processRandomArray(int[] randomArray, int target) {
        int count = 0;
        for (int value : randomArray) {
            if (value < target) { // Unpredictable: random true/false
                count++;
            }
        }
        return count;
    }
}

// Cache-friendly vs Cache-unfriendly patterns

// 1. Sequential Access (Cache-friendly)
public class SequentialAccess {
    public int sumMatrixSequential(int[][] matrix) {
        int sum = 0;
        int rows = matrix.length;
        int cols = matrix[0].length;

        // Row-major order - cache friendly
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                sum += matrix[i][j];
            }
        }
        return sum;
    }
}

// 2. Random Access (Cache-unfriendly)
public class RandomAccess {
    public int sumMatrixRandom(int[][] matrix) {
        int sum = 0;
        int rows = matrix.length;
        int cols = matrix[0].length;

        // Column-major order - cache unfriendly
        for (int j = 0; j < cols; j++) {
            for (int i = 0; i < rows; i++) {
                sum += matrix[i][j];
            }
        }
        return sum;
    }
}

// 3. Object Pool Pattern for Memory Efficiency
public class ObjectPool<T> {
    private final Queue<T> pool;
    private final Supplier<T> factory;
    private final int maxSize;

    public ObjectPool(Supplier<T> factory, int maxSize) {
        this.factory = factory;
        this.maxSize = maxSize;
        this.pool = new ConcurrentLinkedQueue<>();
    }

    public T acquire() {
        T obj = pool.poll();
        if (obj == null) {
            obj = factory.get();
        }
        return obj;
    }

    public void release(T obj) {
        if (pool.size() < maxSize) {
            pool.offer(obj);
        }
    }
}

// 4. Memory-mapped Files for Large Data
import java.nio.*;
import java.nio.channels.*;
import java.io.*;

public class MemoryMappedFile {
    private MappedByteBuffer buffer;
    private FileChannel channel;

    public void mapFile(String filename) throws IOException {
        RandomAccessFile file = new RandomAccessFile(filename, "rw");
        channel = file.getChannel();

        // Map entire file to memory
        buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, channel.size());
    }

    public void writeData(byte[] data, long position) {
        buffer.position((int) position);
        buffer.put(data);
    }

    public byte[] readData(int size, long position) {
        buffer.position((int) position);
        byte[] data = new byte[size];
        buffer.get(data);
        return data;
    }

    public void close() throws IOException {
        if (channel != null) {
            channel.close();
        }
    }
}

// 5. String Optimization
public class StringOptimization {
    // Bad: String concatenation in loop
    public String buildStringBad(String[] parts) {
        String result = "";
        for (String part : parts) {
            result += part; // Creates new String object each time!
        }
        return result;
    }

    // Good: StringBuilder
    public String buildStringGood(String[] parts) {
        StringBuilder sb = new StringBuilder();
        for (String part : parts) {
            sb.append(part);
        }
        return sb.toString();
    }

    // Better: Pre-sized StringBuilder
    public String buildStringBetter(String[] parts) {
        int totalLength = 0;
        for (String part : parts) {
            totalLength += part.length();
        }

        StringBuilder sb = new StringBuilder(totalLength);
        for (String part : parts) {
            sb.append(part);
        }
        return sb.toString();
    }
}

import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicLong;

// Custom benchmarking framework
public class Benchmark {
    private final AtomicLong totalTime = new AtomicLong(0);
    private final AtomicLong iterations = new AtomicLong(0);

    public void measure(String name, Runnable task) {
        long startTime = System.nanoTime();
        try {
            task.run();
        } finally {
            long endTime = System.nanoTime();
            long duration = endTime - startTime;

            totalTime.addAndGet(duration);
            iterations.incrementAndGet();

            System.out.printf("%s: %d ns (%.2f ms)\n", 
                name, duration, duration / 1_000_000.0);
        }
    }

    public void measureAverage(String name, Runnable task, int iterations) {
        long totalDuration = 0;

        // Warmup
        for (int i = 0; i < 10; i++) {
            task.run();
        }

        // Actual measurement
        for (int i = 0; i < iterations; i++) {
            long startTime = System.nanoTime();
            task.run();
            long endTime = System.nanoTime();
            totalDuration += (endTime - startTime);
        }

        double averageNs = totalDuration / (double) iterations;
        System.out.printf("%s (avg over %d iterations): %.2f ns (%.4f ms)\n", 
            name, iterations, averageNs, averageNs / 1_000_000.0);
    }

    public void printStats() {
        if (iterations.get() > 0) {
            double avgTime = totalTime.get() / (double) iterations.get();
            System.out.printf("Total iterations: %d, Average time: %.2f ns\n", 
                iterations.get(), avgTime);
        }
    }
}

// Performance comparison example
public class PerformanceComparison {
    private static final int ARRAY_SIZE = 1000000;
    private static final int[] testArray = new int[ARRAY_SIZE];

    static {
        // Initialize test data
        for (int i = 0; i < ARRAY_SIZE; i++) {
            testArray[i] = i;
        }
    }

    public static void main(String[] args) {
        Benchmark benchmark = new Benchmark();

        // Compare different sum implementations
        benchmark.measureAverage("Sequential Sum", () -> {
            int sum = 0;
            for (int value : testArray) {
                sum += value;
            }
            return sum;
        }, 1000);

        benchmark.measureAverage("Parallel Stream Sum", () -> {
            return java.util.Arrays.stream(testArray).parallel().sum();
        }, 1000);

        benchmark.measureAverage("Manual Parallel Sum", () -> {
            int processors = Runtime.getRuntime().availableProcessors();
            int chunkSize = ARRAY_SIZE / processors;

            java.util.concurrent.Future<Integer>[] futures = new java.util.concurrent.Future[processors];
            java.util.concurrent.ExecutorService executor = 
                java.util.concurrent.Executors.newFixedThreadPool(processors);

            for (int i = 0; i < processors; i++) {
                final int start = i * chunkSize;
                final int end = (i == processors - 1) ? ARRAY_SIZE : start + chunkSize;

                futures[i] = executor.submit(() -> {
                    int sum = 0;
                    for (int j = start; j < end; j++) {
                        sum += testArray[j];
                    }
                    return sum;
                });
            }

            int totalSum = 0;
            try {
                for (java.util.concurrent.Future<Integer> future : futures) {
                    totalSum += future.get();
                }
            } catch (Exception e) {
                e.printStackTrace();
            } finally {
                executor.shutdown();
            }

            return totalSum;
        }, 100);
    }
}

