// Custom Collector implementation
public class CustomCollectors {

    // Custom collector to find top N elements
    public static <T extends Comparable<T>> Collector<T, ?, List<T>> topN(int n) {
        return Collector.of(
            () -> new PriorityQueue<>(n), // Supplier
            (queue, item) -> { // Accumulator
                if (queue.size() < n) {
                    queue.offer(item);
                } else if (queue.peek().compareTo(item) < 0) {
                    queue.poll();
                    queue.offer(item);
                }
            },
            (queue1, queue2) -> { // Combiner
                PriorityQueue<T> merged = new PriorityQueue<>(queue1);
                queue2.forEach(merged::offer);
                return merged;
            },
            queue -> queue.stream().sorted(Collections.reverseOrder()).collect(Collectors.toList()) // Finisher
        );
    }

    // Custom collector for grouping with custom key
    public static <T, K> Collector<T, ?, Map<K, List<T>>> groupingByCustom(
            Function<T, K> keyMapper, 
            Function<K, K> keyTransformer) {
        return Collector.of(
            HashMap::new, // Supplier
            (map, item) -> { // Accumulator
                K key = keyTransformer.apply(keyMapper.apply(item));
                map.computeIfAbsent(key, k -> new ArrayList<>()).add(item);
            },
            (map1, map2) -> { // Combiner
                map2.forEach((key, list) -> 
                    map1.merge(key, list, (list1, list2) -> {
                        list1.addAll(list2);
                        return list1;
                    })
                );
                return map1;
            },
            Function.identity() // Finisher
        );
    }
}

