// Functional composition and currying
public class FunctionalComposition {

    // Function composition
    public static <T, U, V> Function<T, V> compose(Function<U, V> f, Function<T, U> g) {
        return x -> f.apply(g.apply(x));
    }

    // Currying example
    public static Function<Integer, Function<Integer, Integer>> add() {
        return x -> y -> x + y;
    }

    // Partial application
    public static Function<Integer, Integer> add5() {
        return add().apply(5);
    }

    // Memoization
    public static <T, R> Function<T, R> memoize(Function<T, R> function) {
        Map<T, R> cache = new ConcurrentHashMap<>();
        return input -> cache.computeIfAbsent(input, function);
    }

    // Usage example
    public static void main(String[] args) {
        // Function composition
        Function<String, Integer> stringLength = String::length;
        Function<Integer, String> intToString = String::valueOf;
        Function<String, String> composed = compose(intToString, stringLength);

        System.out.println(composed.apply("Hello")); // "5"

        // Currying
        Function<Integer, Integer> add5 = add5();
        System.out.println(add5.apply(3)); // 8

        // Memoization
        Function<Integer, Integer> expensiveFunction = memoize(n -> {
            // Simulate expensive computation
            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
            return n * n;
        });

        // First call takes time
        long start = System.currentTimeMillis();
        expensiveFunction.apply(5);
        long firstCall = System.currentTimeMillis() - start;

        // Second call is instant (cached)
        start = System.currentTimeMillis();
        expensiveFunction.apply(5);
        long secondCall = System.currentTimeMillis() - start;

        System.out.println("First call: " + firstCall + "ms");
        System.out.println("Second call: " + secondCall + "ms");
    }
}

