// Advanced CompletableFuture patterns
public class ReactiveProgramming {

    // Async service simulation
    public static class UserService {
        public CompletableFuture<User> getUserById(int id) {
            return CompletableFuture.supplyAsync(() -> {
                try {
                    Thread.sleep(100); // Simulate network call
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                return new User(id, "User" + id);
            });
        }
    }

    public static class OrderService {
        public CompletableFuture<List<Order>> getOrdersByUserId(int userId) {
            return CompletableFuture.supplyAsync(() -> {
                try {
                    Thread.sleep(150); // Simulate network call
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                return IntStream.range(1, 4)
                    .mapToObj(i -> new Order(i, userId, "Order" + i))
                    .collect(Collectors.toList());
            });
        }
    }

    public static class PaymentService {
        public CompletableFuture<Payment> processPayment(int orderId, double amount) {
            return CompletableFuture.supplyAsync(() -> {
                try {
                    Thread.sleep(200); // Simulate payment processing
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                return new Payment(orderId, amount, "SUCCESS");
            });
        }
    }

    // Complex async orchestration
    public CompletableFuture<OrderSummary> processOrder(int userId, double amount) {
        UserService userService = new UserService();
        OrderService orderService = new OrderService();
        PaymentService paymentService = new PaymentService();

        return userService.getUserById(userId)
            .thenCompose(user -> {
                // Get user orders
                CompletableFuture<List<Order>> ordersFuture = orderService.getOrdersByUserId(userId);

                // Process payment for each order
                return ordersFuture.thenCompose(orders -> {
                    List<CompletableFuture<Payment>> paymentFutures = orders.stream()
                        .map(order -> paymentService.processPayment(order.getId(), amount))
                        .collect(Collectors.toList());

                    // Wait for all payments to complete
                    CompletableFuture<List<Payment>> allPayments = CompletableFuture
                        .allOf(paymentFutures.toArray(new CompletableFuture[0]))
                        .thenApply(v -> paymentFutures.stream()
                            .map(CompletableFuture::join)
                            .collect(Collectors.toList()));

                    return allPayments.thenApply(payments -> 
                        new OrderSummary(user, orders, payments));
                });
            })
            .exceptionally(throwable -> {
                System.err.println("Error processing order: " + throwable.getMessage());
                return null;
            });
    }

    // Timeout and fallback patterns
    public CompletableFuture<String> getDataWithTimeout(String source, int timeoutMs) {
        CompletableFuture<String> dataFuture = CompletableFuture.supplyAsync(() -> {
            try {
                Thread.sleep(timeoutMs + 100); // Simulate slow operation
                return "Data from " + source;
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                throw new RuntimeException(e);
            }
        });

        CompletableFuture<String> timeoutFuture = CompletableFuture
            .supplyAsync(() -> {
                try {
                    Thread.sleep(timeoutMs);
                    return "TIMEOUT";
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    throw new RuntimeException(e);
                }
            });

        return dataFuture.applyToEither(timeoutFuture, Function.identity())
            .exceptionally(throwable -> "FALLBACK_DATA");
    }

    // Circuit breaker pattern
    public static class CircuitBreaker {
        private final int failureThreshold;
        private final long timeoutMs;
        private int failureCount = 0;
        private long lastFailureTime = 0;
        private State state = State.CLOSED;

        private enum State { CLOSED, OPEN, HALF_OPEN }

        public CircuitBreaker(int failureThreshold, long timeoutMs) {
            this.failureThreshold = failureThreshold;
            this.timeoutMs = timeoutMs;
        }

        public <T> CompletableFuture<T> execute(Supplier<CompletableFuture<T>> operation) {
            if (state == State.OPEN) {
                if (System.currentTimeMillis() - lastFailureTime > timeoutMs) {
                    state = State.HALF_OPEN;
                } else {
                    return CompletableFuture.failedFuture(
                        new RuntimeException("Circuit breaker is OPEN"));
                }
            }

            return operation.get()
                .whenComplete((result, throwable) -> {
                    if (throwable != null) {
                        handleFailure();
                    } else {
                        handleSuccess();
                    }
                });
        }

        private void handleFailure() {
            failureCount++;
            lastFailureTime = System.currentTimeMillis();

            if (failureCount >= failureThreshold) {
                state = State.OPEN;
            }
        }

        private void handleSuccess() {
            failureCount = 0;
            state = State.CLOSED;
        }
    }

    public static void main(String[] args) {
        ReactiveProgramming app = new ReactiveProgramming();

        // Process order example
        app.processOrder(1, 100.0)
            .thenAccept(summary -> {
                System.out.println("Order processed successfully:");
                System.out.println("User: " + summary.getUser().getName());
                System.out.println("Orders: " + summary.getOrders().size());
                System.out.println("Payments: " + summary.getPayments().size());
            })
            .join();

        // Timeout example
        app.getDataWithTimeout("slow-service", 1000)
            .thenAccept(System.out::println)
            .join();

        // Circuit breaker example
        CircuitBreaker circuitBreaker = new CircuitBreaker(3, 5000);

        for (int i = 0; i < 5; i++) {
            circuitBreaker.execute(() -> {
                if (i < 3) {
                    return CompletableFuture.failedFuture(new RuntimeException("Service error"));
                } else {
                    return CompletableFuture.completedFuture("Success!");
                }
            }).thenAccept(System.out::println)
              .exceptionally(throwable -> {
                  System.out.println("Failed: " + throwable.getMessage());
                  return null;
              });
        }
    }
}

