// Bytecode manipulation with ASM
import org.objectweb.asm.*;

public class BytecodeManipulation {

    public static class MethodProfiler extends ClassVisitor {
        public MethodProfiler(ClassVisitor cv) {
            super(Opcodes.ASM9, cv);
        }

        @Override
        public MethodVisitor visitMethod(int access, String name, String descriptor, 
                                       String signature, String[] exceptions) {
            MethodVisitor mv = super.visitMethod(access, name, descriptor, signature, exceptions);

            if (!name.equals("<init>") && !name.equals("<clinit>")) {
                return new MethodProfilerAdapter(mv, name);
            }

            return mv;
        }
    }

    public static class MethodProfilerAdapter extends MethodVisitor {
        private final String methodName;

        public MethodProfilerAdapter(MethodVisitor mv, String methodName) {
            super(Opcodes.ASM9, mv);
            this.methodName = methodName;
        }

        @Override
        public void visitCode() {
            // Add timing start
            mv.visitFieldInsn(Opcodes.GETSTATIC, "java/lang/System", "out", 
                            "Ljava/io/PrintStream;");
            mv.visitLdcInsn("Starting " + methodName);
            mv.visitMethodInsn(Opcodes.INVOKEVIRTUAL, "java/io/PrintStream", 
                            "println", "(Ljava/lang/String;)V", false);

            super.visitCode();
        }

        @Override
        public void visitInsn(int opcode) {
            if (opcode == Opcodes.RETURN || opcode == Opcodes.IRETURN || 
                opcode == Opcodes.ARETURN || opcode == Opcodes.LRETURN || 
                opcode == Opcodes.DRETURN || opcode == Opcodes.FRETURN) {

                // Add timing end
                mv.visitFieldInsn(Opcodes.GETSTATIC, "java/lang/System", "out", 
                                "Ljava/io/PrintStream;");
                mv.visitLdcInsn("Ending " + methodName);
                mv.visitMethodInsn(Opcodes.INVOKEVIRTUAL, "java/io/PrintStream", 
                                "println", "(Ljava/lang/String;)V", false);
            }

            super.visitInsn(opcode);
        }
    }
}

