// Custom ClassLoader implementation
public class CustomClassLoader extends ClassLoader {
    private final String classPath;

    public CustomClassLoader(String classPath) {
        this.classPath = classPath;
    }

    @Override
    protected Class<?> findClass(String name) throws ClassNotFoundException {
        try {
            byte[] classBytes = loadClassBytes(name);
            return defineClass(name, classBytes, 0, classBytes.length);
        } catch (IOException e) {
            throw new ClassNotFoundException("Could not load class " + name, e);
        }
    }

    private byte[] loadClassBytes(String className) throws IOException {
        String fileName = className.replace('.', '/') + ".class";
        String fullPath = classPath + "/" + fileName;

        try (FileInputStream fis = new FileInputStream(fullPath);
             ByteArrayOutputStream baos = new ByteArrayOutputStream()) {

            byte[] buffer = new byte[1024];
            int bytesRead;
            while ((bytesRead = fis.read(buffer)) != -1) {
                baos.write(buffer, 0, bytesRead);
            }

            return baos.toByteArray();
        }
    }
}

// Advanced Reflection Examples
public class AdvancedReflection {

    // Dynamic method invocation with caching
    private static final Map<String, Method> methodCache = new ConcurrentHashMap<>();

    public static Object invokeMethod(Object target, String methodName, Object... args) {
        try {
            String cacheKey = target.getClass().getName() + "." + methodName;
            Method method = methodCache.computeIfAbsent(cacheKey, key -> {
                try {
                    Class<?>[] paramTypes = new Class[args.length];
                    for (int i = 0; i < args.length; i++) {
                        paramTypes[i] = args[i].getClass();
                    }
                    return target.getClass().getMethod(methodName, paramTypes);
                } catch (NoSuchMethodException e) {
                    throw new RuntimeException(e);
                }
            });

            return method.invoke(target, args);
        } catch (Exception e) {
            throw new RuntimeException("Failed to invoke method " + methodName, e);
        }
    }

    // Generic type erasure and reification
    public static class TypeToken<T> {
        private final Type type;

        protected TypeToken() {
            this.type = ((ParameterizedType) getClass().getGenericSuperclass())
                .getActualTypeArguments()[0];
        }

        public Type getType() {
            return type;
        }

        public Class<T> getRawType() {
            return (Class<T>) getRawType(type);
        }

        private Class<?> getRawType(Type type) {
            if (type instanceof Class) {
                return (Class<?>) type;
            } else if (type instanceof ParameterizedType) {
                return getRawType(((ParameterizedType) type).getRawType());
            } else if (type instanceof GenericArrayType) {
                return Array.newInstance(getRawType(((GenericArrayType) type).getGenericComponentType()), 0).getClass();
            }
            return Object.class;
        }
    }

    // Dynamic proxy for interface implementation
    public static <T> T createProxy(Class<T> interfaceClass, InvocationHandler handler) {
        return (T) Proxy.newProxyInstance(
            interfaceClass.getClassLoader(),
            new Class[]{interfaceClass},
            handler
        );
    }

    // Method interceptor using dynamic proxy
    public static class MethodInterceptor implements InvocationHandler {
        private final Object target;
        private final Map<String, Method> beforeMethods = new HashMap<>();
        private final Map<String, Method> afterMethods = new HashMap<>();

        public MethodInterceptor(Object target) {
            this.target = target;
        }

        public MethodInterceptor addBefore(String methodName, Method beforeMethod) {
            beforeMethods.put(methodName, beforeMethod);
            return this;
        }

        public MethodInterceptor addAfter(String methodName, Method afterMethod) {
            afterMethods.put(methodName, afterMethod);
            return this;
        }

        @Override
        public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {
            String methodName = method.getName();

            // Execute before method
            if (beforeMethods.containsKey(methodName)) {
                beforeMethods.get(methodName).invoke(null);
            }

            // Execute target method
            Object result = method.invoke(target, args);

            // Execute after method
            if (afterMethods.containsKey(methodName)) {
                afterMethods.get(methodName).invoke(null);
            }

            return result;
        }
    }

    // Annotation processing at runtime
    public static class AnnotationProcessor {
        public static void processAnnotations(Class<?> clazz) {
            // Process class-level annotations
            for (Annotation annotation : clazz.getAnnotations()) {
                System.out.println("Class annotation: " + annotation.annotationType().getSimpleName());
            }

            // Process method annotations
            for (Method method : clazz.getDeclaredMethods()) {
                for (Annotation annotation : method.getAnnotations()) {
                    System.out.println("Method " + method.getName() + " has annotation: " + 
                        annotation.annotationType().getSimpleName());
                }
            }

            // Process field annotations
            for (Field field : clazz.getDeclaredFields()) {
                for (Annotation annotation : field.getAnnotations()) {
                    System.out.println("Field " + field.getName() + " has annotation: " + 
                        annotation.annotationType().getSimpleName());
                }
            }
        }
    }

    // Custom annotation for method timing
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.METHOD)
    public @interface Timed {
        String value() default "";
    }

    // Method timing using reflection
    public static class MethodTimer {
        public static void timeMethods(Object target) {
            Class<?> clazz = target.getClass();

            for (Method method : clazz.getDeclaredMethods()) {
                if (method.isAnnotationPresent(Timed.class)) {
                    Timed annotation = method.getAnnotation(Timed.class);
                    String timerName = annotation.value().isEmpty() ? 
                        method.getName() : annotation.value();

                    try {
                        long startTime = System.nanoTime();
                        method.invoke(target);
                        long endTime = System.nanoTime();

                        System.out.printf("%s took %d ns (%.2f ms)\n", 
                            timerName, endTime - startTime, (endTime - startTime) / 1_000_000.0);
                    } catch (Exception e) {
                        System.err.println("Error timing method " + method.getName() + ": " + e.getMessage());
                    }
                }
            }
        }
    }

    public static void main(String[] args) {
        // Type token example
        TypeToken<List<String>> stringListToken = new TypeToken<List<String>>() {};
        System.out.println("Type: " + stringListToken.getType());
        System.out.println("Raw type: " + stringListToken.getRawType());

        // Dynamic proxy example
        List<String> proxyList = createProxy(List.class, (proxy, method, args1) -> {
            System.out.println("Intercepted call to " + method.getName());
            return method.invoke(new ArrayList<>(), args1);
        });

        proxyList.add("Hello");
        System.out.println("Size: " + proxyList.size());

        // Method interceptor example
        List<String> originalList = new ArrayList<>();
        MethodInterceptor interceptor = new MethodInterceptor(originalList)
            .addBefore("add", String.class.getMethod("valueOf", int.class))
            .addAfter("add", String.class.getMethod("valueOf", int.class));

        List<String> interceptedList = createProxy(List.class, interceptor);
        interceptedList.add("Test");
    }
}

// Example class for annotation processing
class ExampleClass {
    @Timed("initialization")
    public void initialize() {
        try {
            Thread.sleep(100);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    @Timed
    public void process() {
        try {
            Thread.sleep(50);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }
}
